import React, { useState } from 'react';
import './LoginModal.css';

const LoginModal = ({ isOpen, onClose }) => {
  const [isSignUp, setIsSignUp] = useState(false);

  const toggleSignUp = () => {
    setIsSignUp(!isSignUp);
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>X</button>
        <h2>{isSignUp ? 'Sign Up' : 'Login'}</h2>
        <form>
          {isSignUp ? (
            <>
              <input type="text" placeholder="Username" className="modal-input" />
              <input type="text" placeholder="Organization" className="modal-input" />
              <input type="password" placeholder="Password" className="modal-input" />
              <input type="password" placeholder="Confirm Password" className="modal-input" />
            </>
          ) : (
            <>
              <input type="text" placeholder="Email" className="modal-input" />
              <input type="password" placeholder="Password" className="modal-input" />
            </>
          )}
          <button type="submit" className="modal-button">{isSignUp ? 'Sign Up' : 'Login'}</button>
        </form>
        <button className="toggle-button" onClick={toggleSignUp}>
          {isSignUp ? 'Already have an account? Login' : "Don't have an account? Sign Up"}
        </button>
      </div>
    </div>
  );
};

export default LoginModal;
