import React, { useState } from 'react';
import './LoginModal.css';

function LoginModal({ showModal, toggleModal }) {
  const [isSignUp, setIsSignUp] = useState(false);

  const toggleSignUp = () => {
    setIsSignUp(!isSignUp);
  };

  if (!showModal) {
    return null;
  }

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={toggleModal}>&times;</span>
        {isSignUp ? (
          <>
            <h2>Sign Up</h2>
            <input type="text" placeholder="Username" className="input-box" />
            <input type="text" placeholder="Organization" className="input-box" />
            <input type="password" placeholder="Password" className="input-box" />
            <input type="password" placeholder="Confirm Password" className="input-box" />
            <button className="submit-button">Sign Up</button>
            <p>Already have an account? <span className="link" onClick={toggleSignUp}>Login</span></p>
          </>
        ) : (
          <>
            <h2>Login</h2>
            <input type="text" placeholder="Username" className="input-box" />
            <input type="password" placeholder="Password" className="input-box" />
            <button className="submit-button">Login</button>
            <p>Don't have an account? <span className="link" onClick={toggleSignUp}>Sign Up</span></p>
          </>
        )}
      </div>
    </div>
  );
}

export default LoginModal;
